#!/usr/bin/env python3
#there is a better way to do this using nt.dict.values()
#sum(dictvalues)
#(nt_dict['G']+nt_dict['C'])/len(seq)

dna=''
#create a new empty dictionary
nt_count={}
#get a set of unique characters in dna
unique=set(dna)

print('unique:',unique) 

#iterate through each unique nucleotide
for nt in unique:
 #count the number of this unique nt in dna
 count=dna.count(nt)

 nt_count[nt]=count

print('nt count', nt_count)

#store each count in a dictionary
nt_count['A']=360
nt_count['T']=370
nt_count['C']=227
nt_count['G']=206

print(nt_count)

#calculate GC content
AT=float(dna.count('A')+dna.count('T'))
ATGC=float(dna.count('A')+dna.count('T')+dna.count('C')+dna.count('G'))
print(AT)
print(type(AT))
print(ATGC)
print(type(ATGC))
print(AT/ATGC)
GCpercent=(AT/ATGC)*100

print('GC content:',GCpercent)



