#!/usr/bin/env python3

dna=''
#create a new empty dictionary
nt_count={}
#get a set of unique characters in dna
unique=set(dna)

print('unique:',unique) 

#iterate through each unique nucleotide
for nt in unique:
 #count the number of this unique nt in dna
 count=dna.count(nt)

 nt_count[nt]=count

print('nt count', nt_count)



